mapper.delete:
delete
from BoardLike
where commID = #{commID}
  and memberID = #{memberID}

mapper.select:
select count(*) as isRed
from BoardLike
where commID = #{commID}
  AND memberID = #{memberID}

SELECT COUNT(*)
FROM BoardLike
WHERE commID = #{commID}

mapper.update:

UPDATE Community
SET commViewCount = commViewCount + 1
WHERE commID = #{commID}
